import React from "react";
import styled from "styled-components";

const Wrapper = styled.section`
  display: grid;
  height: 120vh;
  width: 100%;
  .hero-inner {
    background: url(../images/hero.png);
    background-position: center center;
    background-size: cover;
    margin-left: -1%;
  }
  .hero-inner2 {
    display: grid;
    gap: 15%;
    padding: 20% 5% 5% 5%;
    color: #000;
    text-align: center;
    h1 {
      color: #000;
      line-height: 37px;
      font-size: 22px;
      @media (min-width: 568px) {
        width: 70%;
      }
      @media (min-width: 1200px) {
        font-size: 45px;
        line-height: 40px;
      }
    }
    .h1 {
      position: relative;
      z-index: 1;
      &:before {
        position: absolute;
        background: #4a5af9;
        content: "";
        width: 34%;
        top: 23px;
        /* left: 0; */
        z-index: -1;
        height: 6px;
      }
    }
    p {
      display: none;
      @media (min-width: 568px) {
        display: block;
      }
      @media (min-width: 768px) {
        width: 73%;
      }
      @media (min-width: 1200px) {
        width: 53%;
        line-height: 25px;
      }
    }
    a {
      color: #318379;
      text-decoration: none;
      &:hover {
        text-decoration: underline;
        color: #4ce;
      }
    }
  }
`;

function Hero() {
  return (
    <Wrapper>
      <div className="hero-inner"></div>
      <div className="hero-inner2">
        <h1>
          Tech education & employment, for <h1 className="h1">everyone.</h1>
        </h1>
        <p>
          We believe that everyone, everywhere should have access to life
          changing technology education and employment opportunities.
        </p>
        <button>Donate Today</button>
      </div>
    </Wrapper>
  );
}

export default Hero;
