      a {
        padding: 8px 20px;
      }
    }
    @media (min-width: 768px) {
      max-height: 420px;
      a {
        font-size: 20px;
        padding: 8px 5%;
        &:hover {
          font-size: 24px;
        }
      }
    }
  }
`;

function Header() {
  const [click, setClick] = useState(false);
  return (
    <Wrapper>
      <a href="#" className="logo">
       <img src="./logo192.png" alt="logo"/>
      </a>
      <div className="menu">
        <div
          className={click ? "hamburger open" : "hamburger"}
          onClick={() => setClick(!click)}
        >
          <span></span>
        </div>
        <ul className={click ? "change" : ""}>
          <li>
            <a href="#">
              Home
            </a>
          </li>
          <li>
            <a href="#">
              About
            </a>
          </li>
          <li>
            <a href="#">
              Services
            </a>
          </li>
          <li>
            <a href="#">
              Contact
            </a>
          </li>
        </ul>
      </div>
    </Wrapper>
  );
}

export default Header;
