import React from "react";
import styled from "styled-components";

const Wrapper = styled.section`
  .sustainability {
    padding: 30% 10%;
    flex-wrap: wrap;
    display: grid;
    gap: 5%;
    h2 {
      font-size: 40px;
      @media (min-width: 568px) {
        font-size: 48px;
        font-weight: 700;
      }
      @media (min-width: 768px) {
        font-size: 30px;
        font-weight: 800;
      }
      @media (min-width: 992px) {
        font-size: 40px;
      }
      @media (min-width: 1200px) {
        font-size: 50px;
      }
    }
    .sustain {
      display: grid;
      gap: 10%;
      h3 {
        font-size: 25px;
      }
      p {
        line-height: 25px;
      }
    }
    .sustainability-bottom {
      margin-top: 10%;
      .sustainability-left {
        h3 {
          font-weight: 800;
        }
        button {
          margin-top: 2%;
          color: #fff;
          background: #4a5af9;
          border-radius: 5px;
          padding: 4% 1%;
          font-size: 20px;
          font-weight: 600;
          width: 56%;
          border: none;
          &:hover {
            color: #4a5af9;
            background: transparent;
            border: 1px solid #4a5af9;
          }
          @media (min-width: 568px) {
            font-size: 23px;
            border-radius: 4px;
            font-weight: 500;
            width: 36%;
            padding: 1.5% 1%;
          }
          @media (min-width: 768px) {
            padding: 2%;
            width: 50%;
          }
          @media (min-width: 992px) {
            padding: 2%;
            width: 40%;
          }
          @media (min-width: 1200px) {
            font-size: 20px;
            width: 23%;
            padding: 1.5% 1%;
          }
        }
      }
      .sustainability-right {
        .top {
          padding: 35% 5% 5% 5%;
          .images {
            position: relative;
            @media (min-width: 768px) {
              width: 50%;
            }
            @media (min-width: 992px) {
              width: 40%;
            }
            .blue {
              display: none;
              position: relative;
              @media (min-width: 768px) {
                background: #4a5af9;
                display: block;
                height: 200px;
                width: 100%;
                border-radius: 5px;
                z-index: 1;
              }
              @media (min-width: 992px) {
                height: 200px;
                width: 85%;
              }
              @media (min-width: 992px) {
                height: 200px;
                width: 80%;
              }
            }
            .img1 {
              height: 250px;
              width: 100%;
              object-fit: cover;
              border-radius: 10px;
              box-shadow: 1px 1px 5px 5px #c3c3c3;
              @media (min-width: 568px) {
                height: 250px;
                width: 52%;
              }
              @media (min-width: 768px) {
                border-radius: 5px;
                box-shadow: 2px 2px 20px 2px #c3c3c3;
                height: 200px;
                width: 95%;
                z-index: 2;
                position: absolute;
                top: 10%;
                left: 10%;
              }
              @media (min-width: 992px) {
                width: 85%;
              }
              @media (min-width: 1200px) {
                width: 79%;
                left: 7%;
              }
            }
            .img-a {
              position: absolute;
              bottom: -20%;
              width: 38%;
              right: -2%;
              @media (min-width: 568px) {
                bottom: -25%;
              }
              @media (min-width: 768px) {
                bottom: -30%;
                z-index: 4;
                width: 50%;
                right: -34%;
              }
              @media (min-width: 992px) {
                width: 55%;
                right: -20%;
              }
              @media (min-width: 1200px) {
                bottom: -37%;
                width: 60%;
                right: -13%;
              }
            }
          }
        }
      }
    }
  }
`;

function Sustainability() {
  return (
    <Wrapper>
      <div className="sustainability">
        <h2>Sustainability</h2>

        <div className="sustainability-top sustain">
          <h3>Business Model</h3>
          <p>
            For nonprofits, sustainability is everything. As a team of
            entrepreneurs & out-of-the-box thinkers, we have challenged
            ourselves to come up with creative ways to make impact sustainably
            and at scale.
          </p>
        </div>
        <div className="sustainability-bottom">
          <div className="sustainability-left sustain">
            <h3>Hire Uncommon</h3>
            <p>
              We design, develop and market web products for clients all over
              the world and use the profits to help fund our education mission.
            </p>
            <button>See Portfolio</button>
          </div>
          <div className="sustainability-right">
            <div className="top">
              <div className="images">
                <div className="blue"></div>
                <img src="./images/natasha.png" alt="image" className="img1" />
                <img src="./images/das.png" alt="image" className="img-a" />
              </div>
              <div className="text">
                <h4>
                  The Uncommon team was professional, responsive and went above
                  and beyond to deliver.
                </h4>
              </div>
            </div>
            <div className="bottom">
              <p>- Natasha Chadenga, CEO, DaaS Africa</p>
            </div>
          </div>
        </div>
      </div>
    </Wrapper>
  );
}

export default Sustainability;
