import React from "react";
import styled from "styled-components";

const Wrapper = styled.section`
  display: grid;
  height: 120vh;
  width: 100%;
  .hero-inner {
    background: url(../images/hero.png);
    background-position: center center;
    background-size: cover;
    margin-left: -1%;
    @media (min-width: 568px) {
      height: 50vh;
    }
  }
  .hero-inner2 {
    padding: 20% 5% 5% 5%;
    color: #000;
    text-align: center;
    @media (min-width: 568px) {
      padding: 9% 5% 5% 5%;
    }
    h1 {
      color: #000;
      line-height: 37px;
      font-size: 27px;
      @media (min-width: 568px) {
        font-size: 40px;
        font-weight: 700;
        line-height: 50px;
      }
      @media (min-width: 1200px) {
        font-size: 45px;
        line-height: 40px;
      }
    }
    .h1 {
      position: relative;
      z-index: 1;
      &:before {
        position: absolute;
        background: #4a5af9;
        content: "";
        left: 29%;
        width: 39%;
        top: 25px;
        z-index: -1;
        height: 6px;
        @media (min-width: 568px) {
          width: 36%;
          left: 31%;
          top: 35px;
          height: 9px;
        }
      }
    }
    p {
      margin: 10% 0;
      line-height: 30px;
      @media (min-width: 568px) {
        font-size: 30px;
        font-weight: 300;
        line-height: 50px;
        margin: 4% 0;
      }
    }
    button {
      margin-top: 2%;
      color: #fff;
      background: #4a5af9;
      border-radius: 5px;
      padding: 4%;
      font-size: 20px;
      font-weight: 600;
      border: none;
      &:hover {
        color: #4a5af9;
        background: transparent;
        border: 1px solid #4a5af9;
      }
      @media (min-width: 568px) {
        font-weight: 500;
        padding: 2% 2%;
      }
    }
  }
`;

function Hero() {
  return (
    <Wrapper>
      <div className="hero-inner"></div>
      <div className="hero-inner2">
        <h1>
          Tech education &
           <br/> employment, for <h1 className="h1">everyone.</h1>
        </h1>
        <p>
          We believe that everyone, everywhere should have access to life
          changing technology education and employment opportunities.
        </p>
        <button>Donate Today</button>
      </div>
    </Wrapper>
  );
}

export default Hero;
