import React from "react";
import styled from "styled-components";

const Wrapper = styled.section`
  display: flex;
  justify-content: center;
  align-items: center;
  background: url(../images/background.jpg);
  background-position: center center;
  background-size: cover;
  height: 100vh;
  width: 100%;
  .hero-inner {
    display: grid;
    gap: 15%;
    padding: 5%;
    h1 {
      color: #318379;
      line-height: 30px;
    }
    a {
      color: #318379;
      text-decoration: none;
      &:hover{
        te
      }
    }
  }
`;

function Hero() {
  return (
    <Wrapper>
      <div className="hero-inner">
        <h1>Welcome to Wallasteve Auto & Mechanical Engineering</h1>
        <a href="#">Learn more</a>
      </div>
    </Wrapper>
  );
}

export default Hero;
