import React from "react";
import styled from "styled-components";

const Wrapper = styled.section`
  .hub {
    padding: 5%;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
    @media (min-width: 768px) {
      flex-direction: row;
    }
    .text {
      h2 {
        font-size: 25px;
        @media (min-width: 568px) {
          font-size: 44px;
        }
        @media (min-width: 768px) {
          font-size: 28px;
        }
        @media (min-width: 992px) {
          font-size: 47px;
        }
      }
      p {
        line-height: 25px;
        @media (min-width: 568px) {
          font-size: 30px;
          line-height: 50px;
          font-weight: 300;
        }
        @media (min-width: 768px) {
          font-size: 20px;
          line-height: 35px;
        }
        @media (min-width: 992px) {
          font-size: 19px;
          line-height: 30px;
        }
        @media (min-width: 1200px) {
          font-size: 24px;
          line-height: 42px;
          letter-spacing: 1px;
        }
      }
    }
    .images {
      img {
        width: 100%;
      }
      .img2 {
        display: none;
        @media (min-width: 768px) {
          display: block;
        }
      }
    }
  }
`;

function Hub() {
  return (
    <Wrapper>
      <div className="hub">
        <div className="text">
          <h2>Hub Sponsorship</h2>
          <p>
            We brand our Innovation Hubs for corporate partners, boosting their
            brand awareness, customer loyalty & employee engagement.
          </p>
          <button>See Portfolio</button>
        </div>
        <div className="images">
          <img src="./images/hub1.png" alt="hub1"/>
          <img src="./images/hub2.png" className="img2" alt="hub2"/>
        </div>
      </div>
    </Wrapper>
  );
}

export default Hub;
