import React from "react";
import styled from "styled-components";

const Wrapper = styled.section`
  background: #f2f3fe;
  width: 100%;
  .mentor {
    padding: 15% 0%;
    .mentor-inner {
      padding: 15% 0%;
      background: #fff;
      text-align: center;
      .images {
        position: relative;
        img {
          width: 70%;
        }
        .img-a {
          position: absolute;
          bottom: -25%;
          width: 38%;
          right: 5%;
        }
      }
    }
    .text{
      h3
    }
  }
`;

function Mentor() {
  return (
    <Wrapper>
      <div className="mentor">
      <div className="mentor-inner">
        <div className="images">
          <div className="blue"></div>
          <img src="./images/profile.jpeg" alt="image" className="img" />
          <img src="./images/ad.png" alt="image" className="img-a" />
        </div>
        <div className="text">
          <h4>
            Becoming a Mentor at Uncommon has been an extraordinary experience.
            It's been a privilege to watch Walter grow from novice to
            world-class designer.
          </h4>
          <p>- Rasa Lavarevic, Sr. UX/UI Designer, Autodesk</p>
        </div>
        </div>
      </div>
    </Wrapper>
  );
}

export default Mentor;
