import React from "react";
import styled from "styled-components";

const Wrapper = styled.section`
  background: #f2f3fe;
  width: 100%;
  margin-top: 10%;
  @media (min-width: 568px) {
    margin-top: 4%;
  }
  .mentor {
    padding: 15% 0%;
    @media (min-width: 568px) {
      padding: 9% 0%;
    }

    .mentor-inner {
      padding: 15% 5% 30% 5%;
      background: #fff;
      text-align: center;
      display: grid;
      gap: 9%;
      @media (min-width: 568px) {
        padding-bottom: 2%;
      }
      .images {
        position: relative;
        .img1 {
          height: 250px;
          width: 75%;
          object-fit: cover;
          border-radius: 10px;
          box-shadow: 1px 1px 5px 5px #c3c3c3;
          @media (min-width: 568px) {
            height: 250px;
            width: 52%;
          }
        }
        .img-a {
          position: absolute;
          bottom: -20%;
          width: 38%;
          right: -2%;
          @media (min-width: 568px) {
            bottom: -25%;
          }
        }
      }
    }
    .text {
      display: grid;
      gap: 5%;
      h4 {
        font-size: 15px;
        line-height: 25px;
        @media (min-width: 568px) {
          font-size: 27px;
          line-height: 35px;
        }
      }
      p {
        font-size: 20px;
        line-height: 26px;
      }
    }
  }
`;

function Mentor() {
  return (
    <Wrapper>
      <div className="mentor">
      <div className="mentor-inner">
        <div className="images">
          <div className="blue"></div>
          <img src="./images/profile.jpeg" alt="image" className="img1" />
          <img src="./images/ad.png" alt="image" className="img-a" />
        </div>
        <div className="text">
          <h4>
            Becoming a Mentor at Uncommon has been an extraordinary experience.
            It's been a privilege to watch Walter grow from novice to
            world-class designer.
          </h4>
          <p>- Rasa Lavarevic, Sr. UX/UI Designer, Autodesk</p>
        </div>
        </div>
      </div>
    </Wrapper>
  );
}

export default Mentor;
