import React from "react";
import styled from "styled-components";

const Wrapper = styled.section`
  display: grid;
  height: 120vh;
  width: 100%;
  .hero-inner {
    background: url(../images/hero.png);
    background-position: center center;
    background-size: cover;
    margin-left: -1%;
  }
  .hero-inner2 {
    display: grid;
    gap: 15%;
    padding: 5%;
    color: #318379;
    h1 {
      color: #000;
      line-height: 30px;
      @media (min-width: 568px) {
        width: 70%;
      }
      @media (min-width: 1200px) {
        font-size: 45px;
        line-height: 40px;
      }
    }
    p {
      display: none;
      @media (min-width: 568px) {
        display: block;
      }
      @media (min-width: 768px) {
        width: 73%;
      }
      @media (min-width: 1200px) {
        width: 53%;
        line-height: 25px;
      }
    }
    a {
      color: #318379;
      text-decoration: none;
      &:hover {
        text-decoration: underline;
        color: #4ce;
      }
    }
  }
`;

function Hero() {
  return (
    <Wrapper>
      <div className="hero-inner"></div>
      <div className="hero-inner2">
        <h1>Tech education & employment, for everyone.</h1>
        <p>
          We believe that everyone, everywhere should have access to life
          changing technology education and employment opportunities.
        </p>
        <button>Donate Today</button>
      </div>
    </Wrapper>
  );
}

export default Hero;
